curl -XPUT 'http://95.85.7.30:9200/prislovia/prislovie/7032' -d '{"txt": "Ani duše vo mne nebolo! ", "kluc": "", "kategoria": "Strach, bázeň", "utvar": "Porekadlá a úslovia", "slova": "duša "}'
