curl -XPUT 'http://95.85.7.30:9200/prislovia/prislovie/7038' -d '{"txt": "Ňebuj śe, ľem śe treś!", "kluc": "", "kategoria": "Strach, bázeň", "utvar": "Porekadlá a úslovia", "slova": ""}'
curl -XPUT 'http://95.85.7.30:9200/prislovia/prislovie/7039' -d '{"txt": "Bol ako na britvách. — Bol ako na ohnivých ihlách. — Bol ako na šidle. — Bol ako na tŕni. — Bol ako na žeravom uhlí.", "kluc": "", "kategoria": "Strach, bázeň", "utvar": "Porekadlá a úslovia", "slova": "uhlie "}'
