curl -XPUT 'http://95.85.7.30:9200/prislovia/prislovie/7041' -d '{"txt": "Bol by z neho mrcha vojak. ", "kluc": "", "kategoria": "Strach, bázeň", "utvar": "Porekadlá a úslovia", "slova": "vojak "}'
curl -XPUT 'http://95.85.7.30:9200/prislovia/prislovie/7042' -d '{"txt": "Dal sa do prosa. ", "kluc": "", "kategoria": "Strach, bázeň", "utvar": "Porekadlá a úslovia", "slova": "pros (prosenie) "}'
curl -XPUT 'http://95.85.7.30:9200/prislovia/prislovie/7043' -d '{"txt": "Dobre, že ho zrádnik nevychytil. — Dobre, že z nôh nespadol. ", "kluc": "", "kategoria": "Strach, bázeň", "utvar": "Porekadlá a úslovia", "slova": "zrádnik "}'
