curl -XPUT 'http://95.85.7.30:9200/prislovia/prislovie/7061' -d '{"txt": "Mechúrom by ho vystrašil. — Mechúrom by ho i kraj sveta zahnal.", "kluc": "", "kategoria": "Strach, bázeň", "utvar": "Porekadlá a úslovia", "slova": "mechúr "}'
